INSERT INTO `tbl_event_description` (`event_id`, `language_id`, `name`, `date`, `text`) VALUES
  (1, 1, 'Бізнес ланч (знижка 40 %)', '4 липня 2018', '<p class=\"news__text\">В сучасному світі бізнесу, обідня перерва, у кожного, буває в різний час та різні дні тижня. Наш ресторан пропонує альтернативу для усіх - перше, друге та компот ви можете поїсти за спец. ціною на протязі усього дня в любий день тижня!</p>'),
  (1, 2, 'Business lunch (40% discount)', '4 July 2018', '<p class=\"news__text\">In the modern business world, lunch break, for everyone, happens at different times and on different days of the week. Our restaurant have&nbsp;an alternative for everyone - the first, second and compote you can eat for specials price throughout the day on a lovely day of the week!</p>'),
  (2, 1, 'Зробіть попереднє замовлення та отримайте знижку!', '4 липня 2018', '<p class=\"news__text\">В ресторані діє гнучка система знижок на попереднє замовлення!</p>'),
  (2, 2, 'Make a reservation and get a discount!',  '4 July 2018', '<p class=\"news__text\">The restaurant has a flexible system of discounts on pre-order!</p>'),
  (3, 1, 'Літні вихідні у MOJO hall', '4 липня 2018', '<p class=\"news__text\">Вихідні будуть набагато цікавіші, якщо провести їх всією сім&#39;єю в MOJO hall.</p>'),
  (3, 2, 'Summer weekend at MOJO hall',  '4 July 2018', '<p class=\"news__text\">Weekends will be much more interesting if you spend them all the family in the MOJO hall.</p>'),
  (4, 1, 'Захоплюючі вихідні', '4 липня 2018', '<p class=\"news__text\">Поки дорослі насолоджуються їжею і спілкуються з друзями, дітлахи теж будуть зайняті справою. У MOJO hall щосуботи та неділі будуть проходити цікаві майстер-класи.</p>'),
  (4, 2, 'Exciting weekend',  '4 July 2018', '<p class=\"news__text\">As adults enjoy food and communicate with friends, kids will also be busy with the cause. Classic will host interesting master classes every Saturday and Sunday.</p>'),
  (5, 1, 'Хочете смачного, вишуканого та незвичного?', '4 липня 2018', '<p class=\"news__text\">Завітайте до MOJO hall. Завжди свіжа поставка та висока смакова якість.</p>'),
